import React from 'react';
import useBtcPrice from './useBtcPrice';
import { ArrowUp, ArrowDown, RefreshCw } from 'lucide-react';

const LiveBtcPrice: React.FC = () => {
  const { currentPrice, priceChange24h, isLoading, error } = useBtcPrice(60000); // 60 seconds refresh

  const formatPrice = (price: number | null): string => {
    if (price === null) return '--';
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(price);
  };

  const formatPercentage = (value: number | null): string => {
    if (value === null) return '--';
    return `${value >= 0 ? '+' : ''}${value.toFixed(2)}%`;
  };

  const getChangeColor = (value: number | null): string => {
    if (value === null) return 'text-gray-500';
    return value >= 0 ? 'text-green-500' : 'text-red-500';
  };

  const getChangeIcon = (value: number | null) => {
    if (value === null) return null;
    return value >= 0 ? (
      <ArrowUp className="inline-block w-3 h-3" />
    ) : (
      <ArrowDown className="inline-block w-3 h-3" />
    );
  };

  if (error) {
    return (
      <div className="text-red-600 text-sm flex items-center">
        <span>Failed to load price</span>
        <button
          onClick={() => window.location.reload()}
          className="ml-1 text-red-600 hover:text-red-800"
          aria-label="Retry"
        >
          <RefreshCw className="w-3 h-3" />
        </button>
      </div>
    );
  }

  return (
    <div className="flex items-center space-x-3">
      <div className="text-xl font-semibold text-gray-800">
        {isLoading ? '...' : formatPrice(currentPrice)}
      </div>

      {!isLoading && (
        <div className={`text-sm font-medium ${getChangeColor(priceChange24h)} flex items-center`}>
          {getChangeIcon(priceChange24h)}
          <span className="ml-0.5">
            {formatPercentage(priceChange24h)}
          </span>
        </div>
      )}

      {isLoading && (
        <div className="text-sm text-gray-500">
          <RefreshCw className="inline-block w-3 h-3 animate-spin mr-1" />
          Loading...
        </div>
      )}
    </div>
  );
};

export default LiveBtcPrice;
