import { useState, useEffect, useCallback } from 'react';
import axios from 'axios';

export interface TickerItem {
  id: string;
  label: string;
  currentPrice: number | null;
  priceChange24h: number | null;
  currencySymbol?: string;
}

interface BtcPriceData {
  items: TickerItem[];
  isLoading: boolean;
  error: string | null;
  lastUpdated: number | null;
}

// Cache for storing API responses
const CACHE_KEY = 'btcPriceData';
const CACHE_DURATION = 5 * 60 * 1000; // 5 minutes cache

// Helper functions outside the hook
const getCachedData = (): BtcPriceData | null => {
  const cached = localStorage.getItem(CACHE_KEY);
  if (!cached) return null;

  try {
    const { data, timestamp } = JSON.parse(cached);
    // Return cached data if it's still fresh
    if (Date.now() - timestamp < CACHE_DURATION) {
      return data;
    }
  } catch (e) {
    console.error('Error reading cache:', e);
  }
  return null;
};

const saveToCache = (data: BtcPriceData) => {
  try {
    localStorage.setItem(CACHE_KEY, JSON.stringify({
      data,
      timestamp: Date.now()
    }));
  } catch (e) {
    console.error('Error saving to cache:', e);
  }
};

const useBtcPrice = (refreshInterval = 60000): BtcPriceData => {
  const [tickerItems, setTickerItems] = useState<TickerItem[]>([
    { id: 'btc-usd', label: 'BTC/USD', currentPrice: null, priceChange24h: null, currencySymbol: '$' },
    { id: 'btc-eur', label: 'BTC/EUR', currentPrice: null, priceChange24h: null, currencySymbol: '€' },
    { id: 'btc-eth', label: 'BTC/ETH', currentPrice: null, priceChange24h: null, currencySymbol: 'Ξ' },
    { id: 'btc-gold', label: 'Gold (oz)', currentPrice: null, priceChange24h: null, currencySymbol: 'oz' },
  ]);

  const [isLoading, setIsLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [lastUpdated, setLastUpdated] = useState<number | null>(null);

  // Memoized function to update ticker items
  const updateTickerItems = useCallback((newItems: TickerItem[]) => {
    setTickerItems(prevItems => 
      prevItems.map(item => {
        const updatedItem = newItems.find(newItem => newItem.id === item.id);
        return updatedItem ? { ...item, ...updatedItem } : item;
      })
    );
  }, []);

  const fetchBtcPrice = useCallback(async (): Promise<void> => {
    try {
      // Check cache first
      const cachedData = getCachedData();
      if (cachedData) {
        updateTickerItems(cachedData.items);
        setLastUpdated(cachedData.lastUpdated);
        setIsLoading(false);
        setError(null);
        return; // Use cached data if available
      }

      setIsLoading(true);
      setError(null);

      // Fetch BTC prices with rate limit handling
      // Remove unused eurUsdResponse since we don't need it
      const [btcResponse, ethResponse, goldResponse] = await Promise.all([
        // BTC/USD and BTC/EUR
        axios.get('https://api.coingecko.com/api/v3/simple/price', {
          params: {
            ids: 'bitcoin',
            vs_currencies: 'usd,eur',
            include_24hr_change: true,
          },
        }),
        // ETH/USD for BTC/ETH conversion
        axios.get('https://api.coingecko.com/api/v3/simple/price', {
          params: {
            ids: 'ethereum',
            vs_currencies: 'usd',
            include_24hr_change: true,
          },
        }),
        // Gold price in USD (approximate)
        axios.get('https://api.coingecko.com/api/v3/simple/price', {
          params: {
            ids: 'pax-gold',
            vs_currencies: 'usd',
          },
        })
      ]);

      const btcData = btcResponse.data.bitcoin;
      const ethData = ethResponse.data.ethereum;
      const goldData = goldResponse.data['pax-gold'];

      // Calculate BTC/ETH price
      const btcEthPrice = btcData.usd / ethData.usd;
      const btcEthChange24h = ((btcData.usd_24h_change - ethData.usd_24h_change) / 
        (1 + ethData.usd_24h_change / 100)) || 0;

      // Calculate BTC/Gold price (gold price is per gram, converting to oz)
      const goldPricePerOz = goldData.usd * 31.1; // 1 oz = 31.1 grams
      const btcGoldPrice = btcData.usd / goldPricePerOz;



      // Update ticker items with new data
      setTickerItems(prevItems => prevItems.map(item => {
        switch (item.id) {
          case 'btc-usd':
            return { ...item, currentPrice: btcData.usd, priceChange24h: btcData.usd_24h_change };
          case 'btc-eur':
            return { ...item, currentPrice: btcData.eur, priceChange24h: btcData.eur_24h_change };
          case 'btc-eth':
            return { ...item, currentPrice: btcEthPrice, priceChange24h: btcEthChange24h };
          case 'btc-gold':
            return { ...item, currentPrice: btcGoldPrice, priceChange24h: btcData.usd_24h_change };
          default:
            return item;
        }
      }));

      const result = {
        items: tickerItems.map(item => ({
          ...item,
          currentPrice: item.id === 'btc-usd' ? btcData.usd : 
                        item.id === 'btc-eur' ? btcData.eur :
                        item.id === 'btc-eth' ? btcEthPrice :
                        item.id === 'btc-gold' ? btcGoldPrice : item.currentPrice,
          priceChange24h: item.id === 'btc-eth' ? btcEthChange24h : 
                          item.id === 'btc-usd' ? btcData.usd_24h_change :
                          item.id === 'btc-eur' ? btcData.eur_24h_change :
                          item.id === 'btc-gold' ? btcData.usd_24h_change : item.priceChange24h
        })),
        isLoading: false,
        error: null,
        lastUpdated: Date.now()
      };

      setTickerItems(result.items);
      setLastUpdated(result.lastUpdated);
      saveToCache(result);
    } catch (err) {
      console.error('Error fetching price data:', err);
      setError('Failed to fetch price data');
    } finally {
      setIsLoading(false);
    }
  }, []);

  useEffect(() => {
    // Initial fetch
    fetchBtcPrice();

    // Set up effect for fetching data
    const intervalId = setInterval(fetchBtcPrice, refreshInterval);

    // Clean up interval on component unmount
    return () => clearInterval(intervalId);
  }, [refreshInterval, fetchBtcPrice]);



  return {
    items: tickerItems,
    isLoading,
    error,
    lastUpdated,
  };
};

export default useBtcPrice;
