import React, { useEffect, useRef } from 'react';
import useBtcPrice from './useBtcPrice';
import { ArrowUp, ArrowDown, RefreshCw } from 'lucide-react';

const LiveBtcPrice: React.FC = () => {
  const { currentPrice, priceChange24h, isLoading, error } = useBtcPrice(60000);
  const tickerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    // This effect handles the ticker animation
    const ticker = tickerRef.current;
    if (!ticker) return;

    const content = ticker.firstElementChild as HTMLElement;
    if (!content) return;

    // Reset animation and trigger reflow
    content.style.animation = 'none';
    // Force reflow
    void content.offsetHeight;

    // Calculate duration based on content width
    const duration = Math.max(20, content.offsetWidth / 50);
    content.style.animation = `ticker ${duration}s linear infinite`;
  }, [currentPrice, priceChange24h]);

  const formatPrice = (price: number | null): string => {
    if (price === null) return '--';
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(price);
  };

  const formatPercentage = (value: number | null): string => {
    if (value === null) return '--';
    return `${value >= 0 ? '+' : ''}${value.toFixed(2)}%`;
  };

  const getChangeColor = (value: number | null): string => {
    if (value === null) return 'text-gray-400';
    return value >= 0 ? 'text-green-400' : 'text-red-400';
  };

  const getChangeIcon = (value: number | null) => {
    if (value === null) return null;
    return value >= 0 ? (
      <ArrowUp className="inline-block w-3 h-3" />
    ) : (
      <ArrowDown className="inline-block w-3 h-3" />
    );
  };

  if (error) {
    return (
      <div className="text-red-400 text-sm flex items-center justify-center w-full">
        <span>Failed to load BTC price</span>
        <button
          onClick={() => window.location.reload()}
          className="ml-2 hover:text-red-300"
          aria-label="Retry"
        >
          <RefreshCw className="w-3 h-3" />
        </button>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="text-gray-400 text-sm flex items-center justify-center w-full">
        <RefreshCw className="inline-block w-3 h-3 animate-spin mr-2" />
        Loading BTC price...
      </div>
    );
  }

  const price = formatPrice(currentPrice);
  const change = formatPercentage(priceChange24h);
  const changeColor = getChangeColor(priceChange24h);
  const changeIcon = getChangeIcon(priceChange24h);

  // Create a string that will be repeated to create a continuous ticker effect
  const tickerContent = `BTC ${price} • 24h: ${change} • `.repeat(8);

  return (
    <div className="relative w-full overflow-hidden">
      <style dangerouslySetInnerHTML={{
        __html: `
          @keyframes ticker {
            0% { transform: translateX(0); }
            100% { transform: translateX(-50%); }
          }
          .ticker-content {
            display: inline-block;
            white-space: nowrap;
            padding-right: 100%;
            animation: ticker 60s linear infinite;
          }
          .ticker-content:hover {
            animation-play-state: paused;
          }
        `
      }} />

      <div ref={tickerRef} className="ticker-container">
        <div className="ticker-content flex items-center">
          {tickerContent.split('•').map((item, index) => (
            <React.Fragment key={index}>
              <span className="font-medium text-white mr-2">{item.replace('•', '').trim()}</span>
              {index < tickerContent.split('•').length - 1 && (
                <span className={`mx-2 ${changeColor} flex items-center`}>
                  {changeIcon}
                  {change}
                </span>
              )}
            </React.Fragment>
          ))}
        </div>
      </div>
    </div>
  );
};

export default LiveBtcPrice;
