import React, { useEffect, useRef } from 'react';
import useBtcPrice, { TickerItem } from './useBtcPrice';
import { ArrowUp, ArrowDown, RefreshCw } from 'lucide-react';

const LiveBtcPrice: React.FC = () => {
  const { items, isLoading, error } = useBtcPrice(30000);
  const tickerRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    // This effect handles the ticker animation
    const ticker = tickerRef.current;
    if (!ticker) return;

    const content = ticker.firstElementChild as HTMLElement;
    if (!content) return;

    // Reset animation and trigger reflow
    content.style.animation = 'none';
    // Force reflow
    void content.offsetHeight;

    // Calculate duration based on content width
    const duration = Math.max(30, content.offsetWidth / 40);
    content.style.animation = `ticker ${duration}s linear infinite`;
  }, [items]);

  const formatPrice = (item: TickerItem): string => {
    if (item.currentPrice === null) return '--';

    // Special formatting for BTC/ETH
    if (item.id === 'btc-eth') {
      return `${item.currentPrice.toFixed(4)}`;
    }

    // Special formatting for BTC/Gold
    if (item.id === 'btc-gold') {
      return `${item.currentPrice.toFixed(4)}`;
    }

    // Default currency formatting for USD/EUR
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: item.id === 'btc-eur' ? 'EUR' : 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: item.id === 'btc-usd' ? 0 : 2,
    }).format(item.currentPrice);
  };

  const formatPercentage = (value: number | null): string => {
    if (value === null) return '--';
    return `${value >= 0 ? '+' : ''}${value.toFixed(2)}%`;
  };

  const getChangeColor = (value: number | null): string => {
    if (value === null) return 'text-gray-400';
    return value >= 0 ? 'text-green-400' : 'text-red-400';
  };

  const getChangeIcon = (value: number | null) => {
    if (value === null) return null;
    return value >= 0 ? (
      <ArrowUp className="inline-block w-3 h-3" />
    ) : (
      <ArrowDown className="inline-block w-3 h-3" />
    );
  };

  if (error) {
    return (
      <div className="text-red-400 text-sm flex items-center justify-center w-full">
        <span>Failed to load market data</span>
        <button
          onClick={() => window.location.reload()}
          className="ml-2 hover:text-red-300"
          aria-label="Retry"
        >
          <RefreshCw className="w-3 h-3" />
        </button>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="text-gray-400 text-sm flex items-center justify-center w-full">
        <RefreshCw className="inline-block w-3 h-3 animate-spin mr-2" />
        Loading market data...
      </div>
    );
  }

  // Generate ticker items
  const tickerItems = items.map(item => ({
    ...item,
    formattedPrice: formatPrice(item),
    formattedChange: formatPercentage(item.priceChange24h),
    changeColor: getChangeColor(item.priceChange24h),
    changeIcon: getChangeIcon(item.priceChange24h),
  }));

  return (
    <div className="w-full bg-gray-900 h-[60px] flex items-center">
      <div className="w-full overflow-hidden">
        <div ref={tickerRef} className="ticker-container">
          <div className="ticker-content flex items-center h-8">
            {tickerItems.flatMap((item, index) => [
              <div key={item.id} className="flex items-center whitespace-nowrap px-4">
                <span className="font-medium text-white">
                  {item.label}: {item.formattedPrice} {item.currencySymbol}
                </span>
                <span className={`mx-2 ${item.changeColor} flex items-center`}>
                  {item.changeIcon}
                  {item.formattedChange}
                </span>
              </div>,
              <span key={`${item.id}-divider`} className="text-gray-500">â€¢</span>
            ])}
          </div>
        </div>
      </div>
    </div>
  );
};

export default LiveBtcPrice;
