import React from 'react';
import useBtcPrice from './useBtcPrice';
import { ArrowUp, ArrowDown, RefreshCw } from 'lucide-react';

const LiveBtcPrice: React.FC = () => {
  const { currentPrice, priceChange24h, isLoading, error } = useBtcPrice(60000); // 60 seconds refresh

  const formatPrice = (price: number | null): string => {
    if (price === null) return '--';
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }).format(price);
  };

  const formatPercentage = (value: number | null): string => {
    if (value === null) return '--';
    return `${value >= 0 ? '+' : ''}${value.toFixed(2)}%`;
  };

  const getChangeColor = (value: number | null): string => {
    if (value === null) return 'text-gray-500';
    return value >= 0 ? 'text-green-500' : 'text-red-500';
  };

  const getChangeIcon = (value: number | null) => {
    if (value === null) return null;
    return value >= 0 ? (
      <ArrowUp className="inline-block w-4 h-4" />
    ) : (
      <ArrowDown className="inline-block w-4 h-4" />
    );
  };

  if (error) {
    return (
      <div className="bg-red-50 text-red-700 p-3 rounded-md flex items-center">
        <span>{error}</span>
        <button
          onClick={() => window.location.reload()}
          className="ml-2 text-red-700 hover:text-red-900"
          aria-label="Retry"
        >
          <RefreshCw className="w-4 h-4" />
        </button>
      </div>
    );
  }

  return (
    <div className="bg-white p-4 rounded-lg shadow-sm border border-gray-100">
      <h2 className="text-lg font-semibold text-gray-700 mb-2">
        Bitcoin (BTC) Price
      </h2>

      {isLoading && !currentPrice ? (
        <div className="flex items-center justify-center p-4">
          <RefreshCw className="animate-spin h-6 w-6 text-blue-500" />
          <span className="ml-2 text-gray-600">Loading...</span>
        </div>
      ) : (
        <div>
          <div className="text-2xl font-bold text-gray-900">
            {formatPrice(currentPrice)}
          </div>

          <div className={`mt-1 text-sm ${getChangeColor(priceChange24h)}`}>
            {getChangeIcon(priceChange24h)}
            <span className="ml-1">
              {formatPercentage(priceChange24h)} (24h)
            </span>
          </div>

          <div className="mt-2 text-xs text-gray-500">
            Updates every minute
          </div>
        </div>
      )}
    </div>
  );
};

export default LiveBtcPrice;
